// Performance Tracking Dashboard JavaScript
class PerformanceTracker {
    constructor() {
        this.updateInterval = 30000; // 30 seconds
        this.charts = {};
        this.init();
    }

    async init() {
        await this.loadInitialData();
        this.setupRealTimeUpdates();
        this.initializeCharts();
    }

    async loadInitialData() {
        try {
            const [pnlData, metricsData] = await Promise.all([
                fetch('/wp-json/trp-performance/v1/realtime-pnl'),
                fetch('/wp-json/trp-performance/v1/performance-metrics')
            ]);
            this.pnlData = await pnlData.json();
            this.metricsData = await metricsData.json();
            this.updateUI();
        } catch (error) {
            console.error('Failed to load performance data:', error);
        }
    }

    setupRealTimeUpdates() {
        setInterval(() => this.loadInitialData(), this.updateInterval);
    }

    initializeCharts() {
        // Initialize Chart.js charts for performance visualization
        this.createBenchmarkChart();
        this.createMetricsChart();
    }

    createBenchmarkChart() {
        const ctx = document.getElementById('benchmark-chart');
        if (!ctx) return;

        this.charts.benchmark = new Chart(ctx, {
            type: 'line',
            data: {
                labels: [],
                datasets: [{
                    label: 'Portfolio Performance',
                    data: [],
                    borderColor: '#007bff',
                    backgroundColor: 'rgba(0,123,255,0.1)'
                }, {
                    label: 'S&P 500',
                    data: [],
                    borderColor: '#28a745',
                    backgroundColor: 'rgba(40,167,69,0.1)'
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false
            }
        });
    }

    createMetricsChart() {
        const ctx = document.getElementById('metrics-chart');
        if (!ctx) return;

        this.charts.metrics = new Chart(ctx, {
            type: 'radar',
            data: {
                labels: ['Sharpe Ratio', 'Win Rate', 'Max Drawdown', 'Profit Factor'],
                datasets: [{
                    label: 'Current Metrics',
                    data: [],
                    borderColor: '#dc3545',
                    backgroundColor: 'rgba(220,53,69,0.1)'
                }]
            }
        });
    }

    updateUI() {
        // Update P&L display
        const pnlElement = document.querySelector('.pnl-tracker');
        if (pnlElement && this.pnlData) {
            pnlElement.textContent = `$${this.pnlData.total_pnl?.toFixed(2) || '0.00'}`;
            pnlElement.className = `pnl-tracker ${this.pnlData.total_pnl >= 0 ? 'positive' : 'negative'}`;
        }

        // Update metrics cards
        this.updateMetricsCards();

        // Update charts
        this.updateCharts();
    }

    updateMetricsCards() {
        if (!this.metricsData) return;

        const metrics = ['sharpe_ratio', 'win_rate', 'max_drawdown', 'profit_factor'];
        metrics.forEach(metric => {
            const element = document.querySelector(`.metric-${metric}`);
            if (element) {
                element.textContent = this.metricsData[metric]?.toFixed(3) || '0.000';
            }
        });
    }

    updateCharts() {
        if (this.charts.benchmark && this.pnlData?.benchmark_data) {
            this.charts.benchmark.data.labels = this.pnlData.benchmark_data.dates;
            this.charts.benchmark.data.datasets[0].data = this.pnlData.benchmark_data.portfolio;
            this.charts.benchmark.data.datasets[1].data = this.pnlData.benchmark_data.sp500;
            this.charts.benchmark.update();
        }

        if (this.charts.metrics && this.metricsData) {
            this.charts.metrics.data.datasets[0].data = [
                this.metricsData.sharpe_ratio,
                this.metricsData.win_rate,
                this.metricsData.max_drawdown,
                this.metricsData.profit_factor
            ];
            this.charts.metrics.update();
        }
    }
}

// Initialize when DOM is ready
document.addEventListener('DOMContentLoaded', () => {
    window.performanceTracker = new PerformanceTracker();
});
